// Loader local pour lottie-web : charge la lib depuis le CDN si absente
(function () {
  if (window.lottie) return; // déjà chargé
  var s = document.createElement("script");
  s.src = "https://cdnjs.cloudflare.com/ajax/libs/lottie-web/5.12.2/lottie.min.js";
  s.async = true;
  s.onload = function () { console.log("[lottie] chargé via CDN"); };
  s.onerror = function () { console.error("[lottie] échec de chargement CDN"); };
  document.head.appendChild(s);
})();
